snippets.Basic Commands:
// Show databases
show dbs

// Use database
use mydb

// Show collections
show collections

// Drop database
db.dropDatabase()

snippets.Insert:
// Insert one
db.users.insertOne({
    name: "John",
    age: 30,
    email: "john@example.com"
})

// Insert many
db.users.insertMany([
    {name: "Alice", age: 25},
    {name: "Bob", age: 35}
])

snippets.Find:
// Find all
db.users.find()

// Find one
db.users.findOne({name: "John"})

// Find with condition
db.users.find({age: {$gt: 25}})

// Projection
db.users.find({}, {name: 1, email: 1})

// Limit
db.users.find().limit(10)

snippets.Update:
// Update one
db.users.updateOne(
    {name: "John"},
    {$set: {age: 31}}
)

// Update many
db.users.updateMany(
    {age: {$lt: 30}},
    {$inc: {age: 1}}
)

// Replace
db.users.replaceOne(
    {name: "John"},
    {name: "John", age: 32}
)

snippets.Delete:
// Delete one
db.users.deleteOne({name: "John"})

// Delete many
db.users.deleteMany({age: {$lt: 18}})

// Drop collection
db.users.drop()

snippets.Query Operators:
// Comparison
{age: {$gt: 25}}  // Greater than
{age: {$gte: 25}}  // Greater than or equal
{age: {$lt: 30}}  // Less than
{age: {$lte: 30}}  // Less than or equal
{age: {$ne: 25}}  // Not equal
{age: {$in: [25, 30, 35]}}  // In array

// Logical
{$and: [{age: {$gt: 25}}, {age: {$lt: 35}}]}
{$or: [{age: 25}, {age: 30}]}
{$not: {age: {$gt: 25}}}

snippets.Indexes:
// Create index
db.users.createIndex({email: 1})

// Compound index
db.users.createIndex({name: 1, age: -1})

// List indexes
db.users.getIndexes()

// Drop index
db.users.dropIndex("email_1")
