snippets.Basic Commands:
-- Show databases
SHOW DATABASES;

-- Create database
CREATE DATABASE mydb;

-- Use database
USE mydb;

-- Show tables
SHOW TABLES;

-- Describe table
DESCRIBE users;

snippets.Select Data:
-- Select all
SELECT * FROM users;

-- Select specific columns
SELECT name, email FROM users;

-- With condition
SELECT * FROM users WHERE age > 25;

-- Order by
SELECT * FROM users ORDER BY age DESC;

-- Limit
SELECT * FROM users LIMIT 10;

snippets.Delete Data:
-- Delete specific rows
DELETE FROM users WHERE age < 18;

-- Delete all rows
DELETE FROM users;

-- Truncate table
TRUNCATE TABLE users;

snippets.Aggregate Functions:
-- Count
SELECT COUNT(*) FROM users;

-- Sum
SELECT SUM(age) FROM users;

-- Average
SELECT AVG(age) FROM users;

-- Group by
SELECT age, COUNT(*) as count
FROM users
GROUP BY age
HAVING count > 1;
