snippets.Services:
@Injectable({
  providedIn: 'root'
})
export class DataService {
  getData() {
    return this.http.get('/api/data');
  }
}

// Usage in component
constructor(private dataService: DataService) {}

ngOnInit() {
  this.dataService.getData().subscribe(data => {
    this.data = data;
  });
}

