snippets.Functions:
# Define function
greet() {
    echo "Hello $1"
}

# Call function
greet "World"

# Return value
add() {
    return $(($1 + $2))
}

add 5 3
result=$?

snippets.String Operations:
# Length
echo ${#name}

# Substring
echo ${name:0:3}

# Replace
echo ${name/John/Jane}

# Uppercase/Lowercase
echo ${name^^}
echo ${name,,}
