snippets.Loops:
// For loop
for (int i = 0; i < 5; i++) {
    cout << i << endl;
}

// Range-based for
for (auto& item : vec) {
    cout << item << endl;
}

// While
while (x < 10) {
    x++;
}

snippets.Functions:
int add(int a, int b) {
    return a + b;
}

// Function overloading
int add(int a, int b, int c) {
    return a + b + c;
}

// Lambda
auto square = [](int x) { return x * x; };

snippets.If/Else:
if (x > 0) {
    cout << "Positive" << endl;
} else if (x < 0) {
    cout << "Negative" << endl;
} else {
    cout << "Zero" << endl;
}

snippets.Classes:
class Person {
private:
    string name;
    int age;
public:
    Person(string n, int a) : name(n), age(a) {}

    string greet() {
        return "Hi, I'm " + name;
    }
};

Person p("Alice", 25);

