snippets.Arrays & Slices:
// Array
var arr [5]int = [5]int{1, 2, 3, 4, 5}

// Slice
slice := []int{1, 2, 3}
slice = append(slice, 4)
len(slice)  // Length
slice[0:2]  // Subslice

snippets.Loops:
// For loop
for i := 0; i < 5; i++ {
    fmt.Println(i)
}

// Range
for index, value := range slice {
    fmt.Println(index, value)
}

// While-style
for x < 10 {
    x++
}

snippets.Functions:
func add(a int, b int) int {
    return a + b
}

// Multiple returns
func divide(a, b int) (int, error) {
    if b == 0 {
        return 0, errors.New("division by zero")
    }
    return a / b, nil
}

result := add(5, 3)

snippets.If/Else:
if x > 0 {
    fmt.Println("Positive")
} else if x < 0 {
    fmt.Println("Negative")
} else {
    fmt.Println("Zero")
}

// If with statement
if err := doSomething(); err != nil {
    fmt.Println(err)
}

snippets.Structs:
type Person struct {
    Name string
    Age  int
}

p := Person{Name: "John", Age: 30}
fmt.Println(p.Name)

// Method
func (p Person) Greet() string {
    return "Hi, I'm " + p.Name
}

snippets.Goroutines:
