snippets.Loops:
// For loop
for (i in 0..4) {
    println(i)
}

// For-each
fruits.forEach { fruit ->
    println(fruit)
}

// While
while (x < 10) {
    x++
}

snippets.Functions:
fun add(a: Int, b: Int): Int {
    return a + b
}

// Single expression
fun add(a: Int, b: Int) = a + b

val result = add(5, 3)

snippets.If/Else:
if (x > 0) {
    println("Positive")
} else if (x < 0) {
    println("Negative")
} else {
    println("Zero")
}

// If as expression
val result = if (x > 0) "Positive" else "Negative"

snippets.Classes:
class Person(val name: String, var age: Int) {
    fun greet() = "Hi, I'm $name"
}

val person = Person("John", 30)
println(person.greet())

snippets.Null Safety:
var name: String? = null  // Nullable
var name2: String = "John"  // Non-nullable

// Safe call
val length = name?.length

// Elvis operator
val value = name ?: "Default"

// Not-null assertion
val len = name!!.length

snippets.When Expression:
when (x) {
    1 -> println("One")
    2, 3 -> println("Two or Three")
    in 4..10 -> println("Four to Ten")
    else -> println("Other")
}

// As expression
val result = when {
    x > 0 -> "Positive"
    x < 0 -> "Negative"
    else -> "Zero"
}

snippets.Extension Functions:
fun String.addExclamation() = this + "!"

val greeting = "Hello".addExclamation()  // "Hello!"

// List extensions
fun List<Int>.sum() = this.reduce { acc, i -> acc + i }
